// src/components/MainPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const MainPage = () => {
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState("");
  const [loading, setLoading] = useState(false);

  const today = new Date();
  const dayName = today.toLocaleDateString("en-US", { weekday: "long" });
  const date = today.toLocaleDateString();

  // Fetch habits from backend
  const fetchHabits = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:5000/api/habits", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHabits(response.data);
      setLoading(false);
    } catch (error) {
      console.error("Failed to fetch habits:", error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchHabits();
  }, []); // fetchHabits defined here, no missing dependency warning

  const handleAddHabit = async () => {
    if (!newHabit.trim()) return;
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:5000/api/habits",
        { name: newHabit },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setHabits([...habits, response.data]);
      setNewHabit("");
    } catch (error) {
      console.error("Failed to add habit:", error);
    }
  };

  const handleToggle = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.patch(
        `http://localhost:5000/api/habits/${id}/toggle`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setHabits(
        habits.map((habit) =>
          habit._id === id ? { ...habit, completed: !habit.completed } : habit
        )
      );
    } catch (error) {
      console.error("Failed to update habit:", error);
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "0 auto", padding: "20px" }}>
      <h1>Welcome to your Habit Tracker!</h1>
      <p>
        Today is {dayName}, {date}
      </p>

      <div style={{ marginTop: "20px" }}>
        <input
          type="text"
          value={newHabit}
          onChange={(e) => setNewHabit(e.target.value)}
          placeholder="Add new habit"
          style={{ padding: "8px", width: "70%" }}
        />
        <button onClick={handleAddHabit} style={{ padding: "8px 12px", marginLeft: "10px" }}>
          Add
        </button>
      </div>

      {loading ? (
        <p>Loading habits...</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0, marginTop: "20px" }}>
          {habits.map((habit) => (
            <li
              key={habit._id}
              style={{
                display: "flex",
                alignItems: "center",
                marginBottom: "10px",
              }}
            >
              <input
                type="checkbox"
                checked={habit.completed}
                onChange={() => handleToggle(habit._id)}
                style={{ marginRight: "10px" }} // reduced gap
              />
              <span style={{ textDecoration: habit.completed ? "line-through" : "none" }}>
                {habit.name}
              </span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MainPage;
