import { useState, useEffect } from "react";
import axios from "axios";
import HabitCard from "./HabitCard";

const MainPage = () => {
  const [habits, setHabits] = useState([]);
  const [habitName, setHabitName] = useState("");

  const token = localStorage.getItem("token");

  const fetchHabits = async () => {
    try {
      const { data } = await axios.get("http://localhost:5000/api/habits", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setHabits(data);
    } catch (err) {
      alert(err.response?.data?.message || "Server error");
    }
  };

  useEffect(() => {
    fetchHabits();
  }, []); // fetch once on mount

  const handleAddHabit = async () => {
    if (!habitName) return;
    try {
      const { data } = await axios.post("http://localhost:5000/api/habits", { name: habitName }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setHabits(prev => [...prev, data]);
      setHabitName("");
    } catch (err) {
      alert(err.response?.data?.message || "Server error");
    }
  };

  return (
    <div className="main-container">
      <h1>Welcome to your Habit Tracker!</h1>
      <p>Today is {new Date().toLocaleDateString("en-US", { weekday: "long", day: "2-digit", month: "2-digit", year: "numeric" })}</p>

      <div className="add-habit">
        <input value={habitName} onChange={e => setHabitName(e.target.value)} placeholder="Add habit..." />
        <button onClick={handleAddHabit}>Add</button>
      </div>

      <div className="habit-list">
        {habits.map(habit => (
          <HabitCard key={habit._id} habit={habit} fetchHabits={fetchHabits} token={token} />
        ))}
      </div>
    </div>
  );
};

export default MainPage;
