import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";

const MainPage = () => {
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState("");

  const token = localStorage.getItem("token");

  // Fetch habits from backend
  const fetchHabits = useCallback(async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/habits", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHabits(res.data);
    } catch (err) {
      console.error("Failed to fetch habits:", err);
    }
  }, [token]);

  // Add a new habit
  const addHabit = async () => {
    if (!newHabit.trim()) return;
    try {
      await axios.post(
        "http://localhost:5000/api/habits",
        { name: newHabit },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNewHabit("");
      fetchHabits();
    } catch (err) {
      alert(err.response?.data?.message || "Failed to add habit");
    }
  };

  // Mark habit as complete
  const toggleComplete = async (id) => {
    try {
      await axios.patch(
        `http://localhost:5000/api/habits/${id}/toggle`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchHabits();
    } catch (err) {
      alert(err.response?.data?.message || "Failed to update habit");
    }
  };

  useEffect(() => {
    fetchHabits();
  }, [fetchHabits]);

  // Get current day & date
  const today = new Date();
  const day = today.toLocaleString("default", { weekday: "long" });
  const date = today.toLocaleDateString();

  return (
    <div style={styles.container}>
      <h1>Welcome to your Habit Tracker!</h1>
      <p>
        Today is {day}, {date}
      </p>

      <div style={styles.addContainer}>
        <input
          type="text"
          placeholder="Enter new habit"
          value={newHabit}
          onChange={(e) => setNewHabit(e.target.value)}
          style={styles.input}
        />
        <button onClick={addHabit} style={styles.addButton}>
          Add
        </button>
      </div>

      <ul style={styles.habitList}>
        {habits.map((habit) => (
          <li key={habit._id} style={styles.habitItem}>
            <input
              type="checkbox"
              checked={habit.completed}
              onChange={() => toggleComplete(habit._id)}
              style={styles.checkbox}
            />
            <span
              style={{
                ...styles.habitText,
                textDecoration: habit.completed ? "line-through" : "none",
              }}
            >
              {habit.name}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

// Inline styles
const styles = {
  container: {
    maxWidth: "600px",
    margin: "30px auto",
    padding: "20px",
    textAlign: "center",
    fontFamily: "Arial, sans-serif",
  },
  addContainer: {
    display: "flex",
    justifyContent: "center",
    marginBottom: "20px",
  },
  input: {
    padding: "10px",
    width: "60%",
    fontSize: "16px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    marginRight: "10px",
  },
  addButton: {
    padding: "10px 20px",
    fontSize: "16px",
    borderRadius: "4px",
    border: "none",
    backgroundColor: "#4CAF50",
    color: "white",
    cursor: "pointer",
  },
  habitList: {
    listStyleType: "none",
    padding: 0,
  },
  habitItem: {
    display: "flex",
    alignItems: "center",
    marginBottom: "10px",
  },
  checkbox: {
    marginRight: "10px", // small gap between checkbox and text
    width: "18px",
    height: "18px",
  },
  habitText: {
    fontSize: "18px",
  },
};

export default MainPage;
