import React, { useState } from "react";

function MainPage({ user }) {
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState("");

  const addHabit = () => {
    if (newHabit.trim() === "") return;
    setHabits([...habits, newHabit]);
    setNewHabit("");
  };

  const removeHabit = (index) => {
    const updated = habits.filter((_, i) => i !== index);
    setHabits(updated);
  };

  return (
    <div style={styles.container}>
      <h2>Welcome, {user.email}</h2>
      <div style={styles.addContainer}>
        <input
          style={styles.input}
          type="text"
          placeholder="New habit..."
          value={newHabit}
          onChange={(e) => setNewHabit(e.target.value)}
        />
        <button style={styles.addButton} onClick={addHabit}>
          Add
        </button>
      </div>
      <ul style={styles.list}>
        {habits.map((habit, index) => (
          <li key={index} style={styles.listItem}>
            {habit}
            <button style={styles.deleteButton} onClick={() => removeHabit(index)}>
              X
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

const styles = {
  container: {
    width: "400px",
    margin: "50px auto",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    boxShadow: "0px 0px 10px #aaa",
  },
  addContainer: {
    display: "flex",
    gap: "10px",
    marginBottom: "20px",
  },
  input: {
    flex: 1,
    padding: "8px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  addButton: {
    padding: "10px",
    borderRadius: "5px",
    border: "none",
    backgroundColor: "#4CAF50",
    color: "white",
    cursor: "pointer",
  },
  list: {
    listStyleType: "none",
    padding: 0,
  },
  listItem: {
    display: "flex",
    justifyContent: "space-between",
    padding: "8px",
    borderBottom: "1px solid #ccc",
  },
  deleteButton: {
    backgroundColor: "red",
    color: "white",
    border: "none",
    borderRadius: "3px",
    cursor: "pointer",
    padding: "2px 6px",
  },
};

export default MainPage;
