import React, { useState, useEffect } from "react";

export default function MainPage({ token, username, setToken, setUsername }) {
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState("");

  const fetchHabits = async () => {
    const res = await fetch("http://localhost:5000/api/habits", {
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await res.json();
    setHabits(data);
  };

  useEffect(() => { fetchHabits(); }, []);

  const addHabit = async () => {
    if (!newHabit) return;
    await fetch("http://localhost:5000/api/habits", {
      method: "POST",
      headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
      body: JSON.stringify({ name: newHabit }),
    });
    setNewHabit("");
    fetchHabits();
  };

  const deleteHabit = async (id) => {
    await fetch(`http://localhost:5000/api/habits/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    fetchHabits();
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    setToken(null);
    setUsername(null);
  };

  return (
    <div style={styles.container}>
      <h1>{username}'s Habit Tracker</h1>
      <div style={styles.inputBox}>
        <input value={newHabit} onChange={(e) => setNewHabit(e.target.value)} placeholder="New habit" style={styles.input} />
        <button onClick={addHabit} style={styles.addBtn}>Add</button>
      </div>
      <ul style={styles.list}>
        {habits.map(habit => (
          <li key={habit._id} style={styles.item}>
            {habit.name} <button onClick={() => deleteHabit(habit._id)} style={styles.delBtn}>Delete</button>
          </li>
        ))}
      </ul>
      <button onClick={logout} style={styles.logoutBtn}>Logout</button>
    </div>
  );
}

const styles = {
  container: { textAlign: "center", marginTop: "30px", fontFamily: "Arial" },
  inputBox: { marginBottom: "20px" },
  input: { padding: "10px", width: "200px", fontSize: "16px" },
  addBtn: { padding: "10px 20px", marginLeft: "10px", cursor: "pointer" },
  list: { listStyle: "none", padding: 0 },
  item: { margin: "10px 0", fontSize: "18px" },
  delBtn: { marginLeft: "10px", cursor: "pointer" },
  logoutBtn: { marginTop: "20px", padding: "10px 20px", cursor: "pointer" },
};
