import React, { useState } from "react";

export default function LoginRegister({ setToken, setUsername }) {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUser] = useState("");
  const [password, setPass] = useState("");
  const [msg, setMsg] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = `http://localhost:5000/api/users/${isLogin ? "login" : "register"}`;
    try {
      const res = await fetch(url, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      const data = await res.json();
      if (res.ok && isLogin) {
        localStorage.setItem("token", data.token);
        localStorage.setItem("username", data.username);
        setToken(data.token);
        setUsername(data.username);
      } else {
        setMsg(data.message);
      }
    } catch (err) {
      setMsg(err.message);
    }
  };

  return (
    <div style={styles.container}>
      <h1>Habit Tracker</h1>
      <form onSubmit={handleSubmit} style={styles.form}>
        <input placeholder="Username" value={username} onChange={(e) => setUser(e.target.value)} style={styles.input} />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPass(e.target.value)} style={styles.input} />
        <button type="submit" style={styles.button}>{isLogin ? "Login" : "Register"}</button>
      </form>
      <p style={styles.toggle} onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? "New user? Register" : "Already registered? Login"}
      </p>
      {msg && <p>{msg}</p>}
    </div>
  );
}

const styles = {
  container: { textAlign: "center", marginTop: "50px", fontFamily: "Arial" },
  form: { display: "inline-block", marginTop: "20px" },
  input: { display: "block", margin: "10px 0", padding: "10px", width: "250px", fontSize: "16px" },
  button: { padding: "10px 20px", fontSize: "16px", cursor: "pointer" },
  toggle: { color: "blue", cursor: "pointer", marginTop: "10px" },
};
