import React, { useState } from "react";

export default function HabitCard({ habit, onComplete, onDelete, onUpdate }) {
  const [editing, setEditing] = useState(false);
  const [title, setTitle] = useState(habit.title);
  const [note, setNote] = useState(habit.note || "");

  const save = () => {
    onUpdate(habit._id, { title, note });
    setEditing(false);
  };

  return (
    <div className="card">
      {editing ? (
        <>
          <input value={title} onChange={(e) => setTitle(e.target.value)} />
          <input value={note} onChange={(e) => setNote(e.target.value)} />
          <div className="card-actions">
            <button onClick={save}>Save</button>
            <button onClick={() => setEditing(false)}>Cancel</button>
          </div>
        </>
      ) : (
        <>
          <h3>{habit.title}</h3>
          <p className="note">{habit.note}</p>
          <p>Completed: {habit.completedDays?.length || 0}</p>
          <p>Streak: {habit.streak || 0}</p>
          <div className="card-actions">
            <button onClick={() => onComplete(habit._id)}>Mark Today</button>
            <button onClick={() => setEditing(true)}>Edit</button>
            <button onClick={() => onDelete(habit._id)} className="danger">Delete</button>
          </div>
        </>
      )}
    </div>
  );
}
