import React, { useState, useEffect } from "react";
import axios from "axios";

function MainPage() {
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState("");
  const token = localStorage.getItem("token");

  const today = new Date();
  const date = today.toLocaleDateString();
  const day = today.toLocaleDateString("en-US", { weekday: "long" });

  // Redirect if not logged in
  useEffect(() => {
    if (!token) {
      alert("You are not logged in!");
      window.location.href = "/";
    }
  }, [token]);

  // Fetch habits
  const fetchHabits = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/habits", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHabits(res.data);
    } catch (err) {
      console.error("Fetch habits error:", err);
      alert("Failed to fetch habits. Check backend or token.");
    }
  };

  useEffect(() => {
    fetchHabits();
  }, []);

  // Add new habit
  const addHabit = async (e) => {
    e.preventDefault();
    if (!newHabit.trim()) return alert("Enter a habit");
    try {
      const res = await axios.post(
        "http://localhost:5000/api/habits",
        { title: newHabit },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNewHabit("");
      setHabits([res.data, ...habits]);
    } catch (err) {
      console.error("Add habit error:", err);
      alert("Failed to add habit. Make sure backend is running and token is valid.");
    }
  };

  // Toggle habit completion
  const toggleHabit = async (id) => {
    try {
      const res = await axios.put(
        `http://localhost:5000/api/habits/${id}/toggle`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setHabits(habits.map((h) => (h._id === id ? res.data : h)));
    } catch (err) {
      console.error("Toggle habit error:", err);
      alert("Failed to toggle habit");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h1 style={styles.title}>Welcome to your Habit Tracker!</h1>
        <p style={styles.date}>Today is {day}, {date}</p>

        <form onSubmit={addHabit} style={styles.form}>
          <input
            type="text"
            placeholder="Add a new habit..."
            value={newHabit}
            onChange={(e) => setNewHabit(e.target.value)}
            style={styles.input}
          />
          <button type="submit" style={styles.button}>+</button>
        </form>

        <ul style={styles.list}>
          {habits.map((habit) => (
            <li key={habit._id} style={styles.listItem}>
              <label style={styles.label}>
                <input
                  type="checkbox"
                  checked={habit.completed}
                  onChange={() => toggleHabit(habit._id)}
                  style={styles.checkbox}
                />
                <span
                  style={{
                    ...styles.habitText,
                    textDecoration: habit.completed ? "line-through" : "none",
                    color: habit.completed ? "#999" : "#333",
                  }}
                >
                  {habit.title}
                </span>
              </label>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

// ðŸŽ¨ Styles â€” aligned, reduced gap between checkbox and text
const styles = {
  container: {
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    minHeight: "100vh",
    backgroundColor: "#f5f7fa",
    paddingTop: "50px",
  },
  card: {
    backgroundColor: "#ffffff",
    padding: "40px",
    borderRadius: "12px",
    boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
    width: "420px",
    textAlign: "center",
  },
  title: {
    color: "#333",
    marginBottom: "5px",
    fontWeight: "600",
  },
  date: {
    color: "#666",
    marginBottom: "25px",
    fontSize: "14px",
  },
  form: {
    display: "flex",
    justifyContent: "center",
    marginBottom: "25px",
  },
  input: {
    flex: "1",
    padding: "10px 12px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "15px",
    outline: "none",
  },
  button: {
    marginLeft: "10px",
    padding: "10px 16px",
    border: "none",
    borderRadius: "8px",
    backgroundColor: "#4CAF50",
    color: "#fff",
    fontWeight: "bold",
    cursor: "pointer",
    fontSize: "18px",
  },
  list: {
    listStyleType: "none",
    padding: 0,
    margin: 0,
    textAlign: "left",
  },
  listItem: {
    marginBottom: "12px",
    backgroundColor: "#f9fafc",
    padding: "10px 14px",
    borderRadius: "8px",
    boxShadow: "0 1px 3px rgba(0,0,0,0.05)",
  },
  label: {
    display: "flex",
    alignItems: "center",
    gap: "5px", // âœ… reduced gap between checkbox and text
  },
  checkbox: {
    width: "18px",
    height: "18px",
    cursor: "pointer",
    accentColor: "#4CAF50",
    margin: 0,
  },
  habitText: {
    fontSize: "16px",
    fontWeight: "500",
    lineHeight: "20px",
  },
};

export default MainPage;
