import { useEffect, useState } from "react";
import axios from "axios";

export default function MainPage() {
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState("");

  const fetchHabits = async () => {
    const token = localStorage.getItem("token");
    const { data } = await axios.get("http://localhost:5000/api/habits", {
      headers: { Authorization: `Bearer ${token}` }
    });
    setHabits(data);
  };

  const addHabit = async () => {
    if (!newHabit) return;
    const token = localStorage.getItem("token");
    try {
      const { data } = await axios.post("http://localhost:5000/api/habits", { title: newHabit }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setHabits([...habits, data]);
      setNewHabit("");
    } catch (err) {
      alert("Failed to add habit. Make sure backend is running and token is valid.");
    }
  };

  useEffect(() => {
    fetchHabits();
  }, []);

  return (
    <div>
      <h2>My Habits</h2>
      <input placeholder="New habit" value={newHabit} onChange={e => setNewHabit(e.target.value)} />
      <button onClick={addHabit}>Add</button>
      <ul>
        {habits.map(h => <li key={h._id}>{h.title}</li>)}
      </ul>
    </div>
  );
}
